mapper.select:
select count(*) from s_dept
dept_id <> #{dept_id}
and pre_id = #{pre_dept.dept_id} and dept_name=#{dept_name} and state=1 and [s_dept.s_dept]

mapper.update:
[Interceptor] update s_dept
state = #{state}, update_user = #{updateUser}, update_time = #{updateTime},
where dept_id = #{dept_id}
