mapper.select:
select count(*) from s_role 

	role_id <> #{role_id} 

and role_name = #{role_name} and state=1 and [s_role.s_role]

mapper.update:
update s_role
role_name = #{role_name}, memo = #{memo}, update_user = #{updateUser}, update_time = #{updateTime},
where role_id = #{role_id}

update s_role
state = #{state}, update_user = #{updateUser}, update_time = #{updateTime},
where role_id = #{role_id}

mapper.delete:
update s_authorize
state = #{state}, update_user = #{updateUser}, update_time = #{updateTime},
where role_id = #{role_id}
